toggle_show=(item)=>{sibling_dropdown=$(item).next('.dropdown-menu')[0];if(sibling_dropdown.classList.contains("show")){sibling_dropdown.classList.remove("show")
descendant_dropdown=$(sibling_dropdown).find('.dropdown-menu');for(let i=0;i<descendant_dropdown.length;i++){descendant_dropdown[i].classList.remove("show");}}
else{sibling_dropdown.classList.add("show")}};$(document).click(function(){document.querySelectorAll('.btn-group').forEach(item=>{if(!item.classList.contains("unchange")){$(item).children('.dropdown-menu')[0].classList.remove("show");}
else{item.classList.remove("unchange");}
console.log("Hi");});});$(function(){document.querySelectorAll('.my-toggler').forEach(item=>{item.addEventListener('click',()=>{toggle_show(item);});});document.querySelectorAll('.btn-group').forEach(item=>{item.addEventListener('click',()=>{if(!item.classList.contains("unchange")){item.classList.add("unchange")}});});});;